import "../../../App.css";
import React, { Component } from "react";
import { inject, observer } from "mobx-react";
import Button from "react-bootstrap/Button";
import { validateEmail, parseJwt } from "../../../Utils/Utils";
import axios from "axios";
import { BASE_URL_AUTH } from "../../../Constants.js";
import SmallAlert from "../../SmallAlert.jsx";
import LargeAlert from "../../LargeAlert.jsx";
import store from '../../../InteriorDesignStore.js';

@inject("store")
@observer
class ForgetForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      forgetUsername: "",
      forgetNewPassword: "",
      forgetSecurityAnswer: "",
      forgetUsernameFormatError: false,

      isDisabled: false,
      securityQuestion: "",
      isEmailExists: true,
      isPasswordCorrect: true,
      isError: false,
    };

    this.handleForgetUsername = this.handleForgetUsername.bind(this);
    this.handleForgetNewPassword = this.handleForgetNewPassword.bind(this);
    this.handleForgetSecurityAnswer = this.handleForgetSecurityAnswer.bind(this);
  }

  async handleForgetUsername(e) {
    const username = e.target.value;
    const lastLetter = username.slice(-1);  // Get the last letter entered
    this.setState({ forgetUsername: username });

    if (!validateEmail(username)) {
      this.setState({ forgetUsernameFormatError: true });
    } else {
      this.setState({ forgetUsernameFormatError: false });

      // Perform request for the last letter typed
        try {
          const response = await axios.get(`http://localhost:3001/users/question?_username=${lastLetter}`);

          if (response.status === 200 && response.data.question) {
            this.setState({
              securityQuestion: response.data.question,
              isDisabled: true,  // Disable username input when user is found
            });
          }
        } catch (error) {
          if (error.response && error.response.status === 404) {
            // If user is not found, keep input enabled
            this.setState({ isDisabled: false });
          }
        }
    }
  }


  handleForgetNewPassword(e) {
    this.setState({ forgetNewPassword: e.target.value });
  }

  handleForgetSecurityAnswer(e) {
    this.setState({ forgetSecurityAnswer: e.target.value });
  }

  handleLogin = () => {
    store.cambiarInicioDeSesion(true);
  }

  async handleForgetSecurityAnswer(e) {
    e.preventDefault();
    if (
      !this.state.forgetUsernameFormatError &&
      this.state.forgetUsername !== "" &&
      this.state.forgetNewPassword !== ""
    ) {
      this.setState({ isDisabled: true });
      const user = {
        email: this.state.forgetUsername,
        security_answer: this.state.forgetSecurityAnswer,
        new_password: this.state.forgetNewPassword,
      };

      try {
        let res = await axios.post(BASE_URL_AUTH + 'forget', user);
        this.setState({
          isError: false,
          isEmailExists: true,
          isPasswordCorrect: true,
          isDisabled: true,
        });

        const loggedInUser = parseJwt(res.data.token);

        this.props.store.cambiarNombreDeUsuario(loggedInUser.username);
        this.props.store.cambiarTokenDeAcceso(res.data.token);
        this.props.store.cambiarTokenDeSesion(res.data.token);
        this.props.store.cambiarInicioDeSesion(true);

        this.props.store.cambiarModuloLogin(false);

        localStorage.setItem('token', res.data.token);

        this.handleLogin();

        return (window.location.pathname = '/inicio-sesion');
      } catch (e) {
        if (e.response === undefined) {
          console.log(e);
        } else if (e.response.status === 400) {
          this.setState({
            isError: false,
            isEmailExists: false,
            isPasswordCorrect: true,
            isDisabled: false,
          });
        } else if (e.response.status === 401) {
          this.setState({
            isError: false,
            isEmailExists: true,
            isPasswordCorrect: false,
            isDisabled: false,
          });
        } else {
          this.setState({
            isError: true,
            isEmailExists: true,
            isPasswordCorrect: true,
            isDisabled: false,
          });
        }
      }
    }
  }

  render() {
    return (
      <div className="vertical-container">
        <form
          className="login-form"
          onSubmit={(e) => {
            this.handleForgetSecurityAnswer(e);
          }}
        >
          {this.state.forgetUsernameFormatError && (
            <SmallAlert message="Invalid Usuario" variant="info" />
          )}
          <div className="login-form-input-space">
            <input
              placeholder="Usuario"
              variant="secondary"
              type="email"
              value={this.state.forgetUsername}
              onChange={(e) => {
                this.handleForgetUsername(e);
              }}
              className="login-form-input"
              disabled={this.state.isDisabled}  // Disable if user is found
            />
          </div>

          {this.state.securityQuestion && (
            <div className="security-question">
              <p>{this.state.securityQuestion}</p>
            </div>
          )}

          <div className="login-form-input-space">
            <input
              placeholder="Password"
              variant="secondary"
              size="sm"
              type="password"
              value={this.state.forgetNewPassword}
              onChange={(e) => {
                this.handleForgetNewPassword(e);
              }}
              className="login-form-input"
            />
          </div>

          <div className="login-form-input-space">
            <input
              placeholder="Respuesta de seguridad"
              variant="secondary"
              size="sm"
              type="text"
              value={this.state.forgetSecurityAnswer}
              onChange={(e) => {
                this.handleForgetSecurityAnswer(e);
              }}
              className="login-form-input"
            />
          </div>
          <Button
            variant="info"
            className="login-submit-button"
            disabled={this.state.isDisabled}
            type="submit"
            style={{ marginBottom: '-10px' }}
          >
            Cambiar contrase√±a
          </Button>
        </form>

        {!this.state.isEmailExists && (
          <LargeAlert message="Email is not registered" variant="danger" style={{ marginTop: '-10px' }} />
        )}
        {this.state.isError && (
          <LargeAlert message="Some Error Occurred" variant="danger" style={{ marginTop: '-10px' }} />
        )}
        {!this.state.isPasswordCorrect && (
          <LargeAlert message="Password Incorrect" variant="danger" style={{ marginTop: '-10px' }} />
        )}
      </div>
    );
  }
}

export default ForgetForm;
